var positions=[];const navBottom=document.getElementById("navBottom"),setPositions=()=>{positions=[0,document.getElementById("about-h").offsetTop-document.getElementById("about-h").offsetHeight,document.getElementById("contact-h").offsetTop-document.getElementById("contact-h").offsetHeight]};window.addEventListener("resize",setPositions()),window.addEventListener("load",setPositions());const goTo=t=>{window.scrollTo({top:positions[t],behavior:"smooth"}),Active(t)};window.addEventListener("scroll",()=>{window.scrollY+90<positions[1]?Active(0):window.scrollY+90<positions[2]?Active(1):Active(2)});const Active=t=>{for(let e=0;e<document.getElementsByClassName("navbarLink").length;e++)document.getElementsByClassName("navbarLink")[e].classList.remove("active");document.getElementsByClassName("navbarLink")[t].classList.add("active")},setNavBottom=t=>{navBottom.style.transform=`translateY(${t}%)`};document.getElementById("navDropdown").addEventListener("click",()=>{"translateY(0%)"===navBottom.style.transform?setNavBottom(-200):setNavBottom(-50)});